export default function LingualaTranslator() {
  const { data: session } = useSession()
  const [sourceText, setSourceText] = useState('')
  const [translatedText, setTranslatedText] = useState('')
  const [sourceLang, setSourceLang] = useState('en')
  const [targetLang, setTargetLang] = useState('de')
  const [isTranslating, setIsTranslating] = useState(false)
  const [isListening, setIsListening] = useState(false)
  const [selectedDomain, setSelectedDomain] = useState('general')
  const [characterCount, setCharacterCount] = useState(0)
  const [wordCount, setWordCount] = useState(0)

  const sourceTextRef = useRef<HTMLTextAreaElement>(null)
  const recognitionRef = useRef<SpeechRecognition | null>(null)

  // Character and word counting
  useEffect(() => {
    setCharacterCount(sourceText.length)
    setWordCount(sourceText.trim() ? sourceText.trim().split(/\s+/).length : 0)
  }, [sourceText])

  // Speech recognition setup
  useEffect(() => {
    if (typeof window !== 'undefined' && ('SpeechRecognition' in window || 'webkitSpeechRecognition' in window)) {
      const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition
      recognitionRef.current = new SpeechRecognition()
      recognitionRef.current.continuous = true
      recognitionRef.current.interimResults = true

      recognitionRef.current.onresult = (event) => {
        let finalTranscript = ''
        let interimTranscript = ''

        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript
          if (event.results[i].isFinal) {
            finalTranscript += transcript
          } else {
            interimTranscript += transcript
          }
        }

        if (finalTranscript) {
          setSourceText(prev => prev + finalTranscript)
        }
      }

      recognitionRef.current.onerror = () => {
        setIsListening(false)
        toast({
          title: "Speech Recognition Error",
          description: "Could not recognize speech. Please try again.",
          variant: "destructive"
        })
      }

      recognitionRef.current.onend = () => {
        setIsListening(false)
      }
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop()
      }
    }
  }, [])

  const handleTranslate = useCallback(async () => {
    if (!sourceText.trim()) {
      toast({
        title: "Input Required",
        description: "Please enter text to translate.",
        variant: "destructive"
      })
      return
    }

    // Check character limit for anonymous users
    if (!session?.user && sourceText.length > 5000) {
      toast({
        title: "Character Limit Exceeded",
        description: "Anonymous users are limited to 5,000 characters. Sign in to translate longer texts.",
        variant: "destructive"
      })
      return
    }

    setIsTranslating(true)

    try {
      const response = await fetch('/api/translate', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          text: sourceText,
          sourceLang,
          targetLang,
          domain: session?.user ? selectedDomain : 'general'
        }),
      })

      if (!response.ok) {
        throw new Error(`HTTP error! status: ${response.status}`)
      }

      const data = await response.json()
      setTranslatedText(data.translatedText)

      toast({
        title: "Translation Complete",
        description: `Translated ${characterCount} characters from ${LANGUAGES.find(l => l.code === sourceLang)?.name} to ${LANGUAGES.find(l => l.code === targetLang)?.name}`,
      })

    } catch (error) {
      console.error('Translation error:', error)
      toast({
        title: "Translation Failed",
        description: "Could not translate text. Please try again later.",
        variant: "destructive"
      })
    } finally {
      setIsTranslating(false)
    }
  }, [sourceText, sourceLang, targetLang, selectedDomain, session
