edits.oldString:
{LANGUAGES.map(lang => (
  <SelectItem key={lang.code} value={lang.code}>
    <div className="flex items-center space-x-2">
      <span>{lang.name}</span>
      {lang.region !== "auto" && (
        <Badge variant="outline" className="text-xs">{lang.region}</Badge>
      )}
    </div>
  </SelectItem>
))}
