export async function POST(request: NextRequest) {
  try {
    const { text, sourceLang, targetLang, domain, glossary } = await request.json()

    if (!text || !targetLang) {
      return NextResponse.json(
        { error: 'Text and target language are required' },
        { status: 400 }
      )
    }

