metadata.preview:
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${apiKey}`,
    'Content-Type': 'application/json',
  },
  body: JSON.stringify({
    model: 'qwen-turbo',
    messages: [{
      role: 'user',
      content: `Translate the following text from ${sourceLanguage} to ${targetLanguage}. Only provide the translation, no explanations:\n\n"${text}"`
    }],
    temperature: 0.1,
    max_tokens: 1000
  }),
})
