callbacks: {
  async session({ session, user }) {
    if (session?.user) {
      session.user.id = user.id
    }
    return session
  },
},

input.newString:
callbacks: {
  async jwt({ token, user }) {
    if (user) {
      token.id = user.id
    }
    return token
  },
  async session({ session, token }) {
    if (session?.user) {
      session.user.id = token.id as string
    }
    return session
  },
},

