Now let me create the document translation page:

<write_to_file>
<path>app/documents/page.tsx</path>
<content>"use client";

import { useState } from "react";
import { DocumentUploader } from "@/components/documents/document-uploader";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Progress } from "@/components/ui/progress";
import { Download, FileText, Languages, Upload } from "lucide-react";

interface DocumentTranslation {
  id: string;
  originalName: string;
  status: 'uploading' | 'processing' | 'translating' | 'completed' | 'error';
  progress: number;
  sourceLanguage?: string;
  targetLanguage?: string;
  downloadUrl?: string;
  error?: string;
}

export default function DocumentsPage() {
  const [translations, setTranslations] = useState<DocumentTranslation[]>([]);

  const handleFileUpload = async (files: File[]) => {
    const newTranslations = files.map(file => ({
      id: Math.random().toString(36).substring(7),
      originalName: file.name,
      status: 'uploading' as const,
      progress: 0,
    }));

    setTranslations(prev => [...prev, ...newTranslations]);

    // Process each file
    for (const translation of newTranslations) {
      const file = files.find(f => f.name === translation.originalName);
      if (!file) continue;

      try {
        // Update status to processing
        setTranslations(prev => 
          prev.map(t => 
            t.id === translation.id 
              ? { ...t, status: 'processing', progress: 25 }
              : t
          )
        );

        // Upload file
        const formData = new FormData();
        formData.append('file', file);

        const uploadResponse = await fetch('/api/documents/upload', {
          method: 'POST',
          body: formData,
        });

        if (!uploadResponse.ok) {
          throw new Error('Upload failed');
        }

        const { fileId } = await uploadResponse.json();

        // Update status to translating
        setTranslations(prev => 
          prev.map(t => 
            t.id === translation.id 
              ? { ...t, status: 'translating', progress: 50 }
              : t
          )
        );

        // Translate document (using default languages for now)
        const translateResponse = await fetch('/api/documents/translate', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify({
            fileId,
            sourceLanguage: 'auto',
            targetLanguage: 'es', // Spanish as default target
          }),
        });

        if (!translateResponse.ok) {
          throw new Error('Translation failed');
        }

        const { translatedFileId } = await translateResponse.json();

        // Update status to completed
        setTranslations(prev => 
          prev.map(t => 
            t.id === translation.id 
              ? { 
                  ...t, 
                  status: 'completed', 
                  progress: 100,
                  sourceLanguage: 'auto',
                  targetLanguage: 'Spanish',
                  downloadUrl: `/api/documents/download/${translatedFileId}`
                }
              : t
          )
        );

      } catch (error) {
        setTranslations(prev => 
          prev.map(t => 
            t.id === translation.id 
              ? { 
                  ...t, 
                  status: 'error', 
                  error: error instanceof Error ? error.message : 'Translation failed'
                }
              : t
          )
        );
      }
    }
  };

  const getStatusColor = (status: DocumentTranslation['status']) => {
    switch (status) {
      case 'uploading': return 'bg-blue-500';
      case 'processing': return 'bg-yellow-500';
      case 'translating': return 'bg-purple-500';
      case 'completed': return 'bg-green-500';
      case 'error': return 'bg-red-500';
      default: return 'bg-gray-500';
    }
  };

  const getStatusText = (status: DocumentTranslation['status']) => {
    switch (status) {
      case 'uploading': return 'Uploading...';
      case 'processing': return 'Processing document...';
      case 'translating': return 'Translating content...';
      case 'completed': return 'Translation completed';
      case 'error': return 'Error occurred';
      default: return status;
    }
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="mb-8">
        <h1 className="text-3xl font-bold mb-2">Document Translation</h1>
        <p className="text-muted-foreground">
          Upload PDF and Word documents to translate while preserving their original formatting
        </p>
      </div>

      {/* Upload Section */}
      <Card className="mb-8">
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Upload className="h-5 w-5" />
            Upload Documents
          </CardTitle>
          <CardDescription>
            Supports PDF and Word documents up to 10MB. Drag and drop multiple files or click to select.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <DocumentUploader onFilesSelected={handleFileUpload} />
        </CardContent>
      </Card>

      {/* Translation Status */}
      {translations.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Languages className="h-5 w-5" />
              Translation Status
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-4">
              {translations.map((translation) => (
                <div 
                  key={translation.id}
                  className="border rounded-lg p-4 space-y-3"
                >
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-3">
                      <FileText className="h-5 w-5 text-muted-foreground" />
                      <div>
                        <p className="font-medium">{translation.originalName}</p>
                        {translation.sourceLanguage && translation.targetLanguage && (
                          <p className="text-sm text-muted-foreground">
                            {translation.sourceLanguage} â†’ {translation.targetLanguage}
                          </p>
                        )}
                      </div>
                    </div>
                    <div className="flex items-center gap-2">
                      <Badge 
                        variant="secondary"
                        className={`${getStatusColor(translation.status)} text-white`}
                      >
                        {getStatusText(translation.status)}
                      </Badge>
                      {translation.status === 'completed' && translation.downloadUrl && (
                        <Button 
                          size="sm" 
                          asChild
                          className="ml-2"
                        >
                          <a href={translation.downloadUrl} download>
                            <Download className="h-4 w-4 mr-1" />
                            Download
                          </a>
                        </Button>
                      )}
                    </div>
                  </div>

                  {translation.status !== 'completed' && translation.status !== 'error' && (
                    <Progress value={translation.progress} className="w-full" />
                  )}

                  {translation.error && (
                    <div className="bg-red-50 border border-red-200 rounded-md p-3">
                      <p className="text-red-800 text-sm">{translation.error}</p>
                    </div>
                  )}
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Features Info */}
      <Card className="mt-8">
        <CardHeader>
          <CardTitle>Features</CardTitle>
        </CardHeader>
        <CardContent>
          <div className="grid md:grid-cols-3 gap-4">
            <div className="text-center p-4">
              <FileText className="h-8 w-8 mx-auto mb-2 text-blue-500" />
              <h3 className="font-semibold">Format Preservation</h3>
              <p className="text-sm text-muted-foreground">
                Maintains original document layout and formatting
              </p>
            </div>
            <div className="text-center p-4">
              <Languages className="h-8 w-8 mx-auto mb-2 text-green-500" />
              <h3 className="font-semibold">25+ Languages</h3>
              <p className="text-sm text-muted-foreground">
                Support for major world languages with auto-detection
              </p>
            </div>
            <div className="text-center p-4">
              <Upload className="h-8 w-8 mx-auto mb-2 text-purple-500" />
              <h3 className="font-semibold">Batch Processing</h3>
              <p className="text-sm text-muted-foreground">
                Upload and translate multiple documents simultaneously
              </p>
            </div>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}
</content>
</write_to_file>

