<content>"use client";

import { useCallback, useState } from "react";
import { useDropzone } from "react-dropzone";
import { Button } from "@/components/ui/button";
import { Card } from "@/components/ui/card";
import { Progress } from "@/components/ui/progress";
import { Alert, AlertDescription } from "@/components/ui/alert";
import { Upload, File, X, AlertCircle, Check } from "lucide-react";
import { cn } from "@/lib/utils";

interface DocumentUploaderProps {
  onFilesSelected: (files: File[]) => void;
  maxSize?: number; // in bytes
  acceptedTypes?: string[];
}

interface FileWithPreview extends File {
  preview?: string;
  id: string;
  status: 'pending' | 'valid' | 'invalid';
  error?: string;
}

export function DocumentUploader({ 
  onFilesSelected,
  maxSize = 10 * 1024 * 1024, // 10MB default
  acceptedTypes = [
    'application/pdf',
    'application/vnd.openxmlformats-officedocument.wordprocessingml.document',
    'application/msword'
  ]
}: DocumentUploaderProps) {
  const [selectedFiles, setSelectedFiles] = useState<FileWithPreview[]>([]);
  const [uploadProgress, setUploadProgress] = useState(0);
  const [isUploading, setIsUploading] = useState(false);

  const validateFile = useCallback((file: File): { valid: boolean; error?: string } => {
    // Check file size
    if (file.size > maxSize) {
      const maxSizeMB = Math.round(maxSize / (1024 * 1024));
      return { valid: false, error: `File too large. Maximum size: ${maxSizeMB}MB` };
    }

    // Check file type
    if (!acceptedTypes.includes(file.type)) {
      return { valid: false, error: 'Unsupported file type. Please upload PDF or Word documents.' };
    }

    return { valid: true };
  }, [maxSize, acceptedTypes]);

  const onDrop = useCallback((acceptedFiles: File[]) => {
    const filesWithValidation = acceptedFiles.map((file) => {
      const validation = validateFile(file);
      const fileWithPreview: FileWithPreview = {
        ...file,
        id: Math.random().toString(36).substring(7),
        status: validation.valid ? 'valid' : 'invalid',
        error: validation.error,
      };
      return fileWithPreview;
    });

    setSelectedFiles(prev => [...prev, ...filesWithValidation]);
  }, [validateFile]);

  const { getRootProps, getInputProps, isDragActive, isDragReject } = useDropzone({
    onDrop,
    accept: {
      'application/pdf': ['.pdf'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'application/msword': ['.doc']
    },
    maxSize,
    multiple: true,
  });

  const removeFile = (fileId: string) => {
    setSelectedFiles(prev => prev.filter(file => file.id !== fileId));
  };

  const handleUpload = async () => {
    const validFiles = selectedFiles.filter(file => file.status === 'valid');
    if (validFiles.length === 0) return;

    setIsUploading(true);
    setUploadProgress(0);

    // Simulate progress for better UX
    const progressInterval = setInterval(() => {
      setUploadProgress(prev => Math.min(prev + 10, 90));
    }, 100);

    try {
      await onFilesSelected(validFiles);
      setUploadProgress(100);
      setSelectedFiles([]); // Clear files after successful upload
    } catch (error) {
      console.error('Upload failed:', error);
    } finally {
      clearInterval(progressInterval);
      setIsUploading(false);
      setTimeout(() => setUploadProgress(0), 1000);
    }
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const getFileIcon = (type: string) => {
    if (type.includes('pdf')) return 'ðŸ“„';
    if (type.includes('word') || type.includes('document')) return 'ðŸ“';
    return 'ðŸ“„';
  };

  const validFiles = selectedFiles.filter(file => file.status === 'valid');

  return (
    <div className="space-y-4">
      {/* Dropzone */}
      <Card
        {...getRootProps()}
        className={cn(
          "border-2 border-dashed p-8 text-center cursor-pointer transition-colors",
          isDragActive && !isDragReject && "border-blue-500 bg-blue-50",
          isDragReject && "border-red-500 bg-red-50",
          "hover:border-gray-400"
        )}
      >
        <input {...getInputProps()} />
        <div className="flex flex-col items-center gap-4">
          <Upload className={cn(
            "h-12 w-12",
            isDragActive && !isDragReject && "text-blue-500",
            isDragReject && "text-red-500",
            !isDragActive && "text-gray-400"
          )} />

