/**
 * Translation API Status Check
 * 
 * Provides status information about the translation service configuration.
 * Useful for debugging and verifying DASHSCOPE_API_KEY setup.
 */
import { NextResponse } from 'next/server'
import { verifyApiConfiguration } from '@/lib/translation-service'

export async function GET() {
  try {
    const config = verifyApiConfiguration()

    return NextResponse.json({
      status: 'ok',
      translation_service: {
        configured: config.configured,
        api_provider: 'Alibaba Cloud DashScope',
        model: 'qwen-mt-turbo',
        key_preview: config.keyPreview,
        error: config.error
      },
      features: {
        text_translation: config.configured,
        document_translation: config.configured,
        supported_formats: ['PDF', 'DOCX', 'TXT'],
        max_file_size: '10MB'
      },
      timestamp: new Date().toISOString()
    })
  } catch (error) {
    return NextResponse.json({
      status: 'error',
      error: 'Failed to check translation service status',
      details: error instanceof Error ? error.message : 'Unknown error'
    }, { status: 500 })
  }
}
