input.oldString:
const copyToClipboard = async (text: string) => {
  try {
    await navigator.clipboard.writeText(text)
    setCopySuccess(true)
    toast.success("Copied to clipboard!")
    setTimeout(() => setCopySuccess(false), 2000)
  } catch (error) {
    toast.error("Failed to copy to clipboard")
  }
}

