    --border: 223 47% 11%;
    --input: 223 47% 11%;
    --ring: 217 91% 60%;
    --chart-1: 217 91% 60%;
    --chart-2: 262 83% 58%;
    --chart-3: 188 95% 68%;
    --chart-4: 280 100% 70%;
    --chart-5: 340 82% 52%;

    /* Custom Linguala variables for dark mode */
    --linguala-primary: 217 91% 60%;
    --linguala-secondary: 262 83% 58%;
    --linguala-accent: 188 95% 68%;
    --linguala-success: 142 71% 45%;
    --linguala-warning: 43 96% 56%;
    --linguala-error: 0 84% 60%;
  }
}

