.linguala-blur-bg {
  background: rgba(255, 255, 255, 0.95);
  backdrop-filter: blur(20px);
  -webkit-backdrop-filter: blur(20px);
}

.dark .linguala-blur-bg {
  background: rgba(13, 16, 23, 0.95);
}

.linguala-glass {
  background: rgba(255, 255, 255, 0.1);
  backdrop-filter: blur(10px);
  -webkit-backdrop-filter: blur(10px);
  border: 1px solid rgba(255, 255, 255, 0.2);
}

.dark .linguala-glass {
  background: rgba(255, 255, 255, 0.05);
  border: 1px solid rgba(255, 255, 255, 0.1);
}

.linguala-rainbow-border {
  background: linear-gradient(white, white) padding-box,
              linear-gradient(135deg, #3B82F6, #8B5CF6, #06B6D4, #10B981, #F59E0B) border-box;
  border: 2px solid transparent;
}

.dark .linguala-rainbow-border {
  background: linear-gradient(hsl(224 71% 4%), hsl(224 71% 4%)) padding-box,
              linear-gradient(135deg, #3B82F6, #8B5CF6, #06B6D4, #10B981, #F59E0B) border-box;
}

