input.command:
cd /home/user/project && curl -X POST "https://dashscope-intl.aliyuncs.com/compatible-mode/v1/chat/completions" \
  -H "Authorization: Bearer sk-ad9404d1ced5426082b73e685a95ffa3" \
  -H "Content-Type: application/json" \
  -d '{
    "model": "qwen-max",
    "messages": [
      {
        "role": "system",
        "content": "You are a professional writing assistant. Improve the given text by enhancing clarity, grammar, style, and readability. Return only the improved text."
      },
      {
        "role": "user",
        "content": "This is a test text that need some improvement on grammar."
      }
    ],
    "max_tokens": 100,
    "temperature": 0.3
  }' --timeout 10

