const response = await fetch('https://dashscope-intl.aliyuncs.com/compatible-mode/v1/chat/completions', {
  method: 'POST',
  headers: {
    'Authorization': `Bearer ${DASHSCOPE_API_KEY}`,
    'Content-Type': 'application/json',
  },
  body: JSON.stringify({
    model: 'qwen-mt-turbo',
    messages: [
      {
        role: 'user',
        content: `Translate from ${sourceLang === 'auto' ? 'auto-detected language' : (LANGUAGE_MAP[sourceLang] || sourceLang)} to ${LANGUAGE_MAP[targetLang] || targetLang}: ${text}`
      }
    ],
    max_tokens: 200,
    temperature: 0.1
  }),
  signal: controller.signal
})

